import React from 'react'
import './Main.css'
import  Tab from './Tab';
import TopBlog from './TopBlog';

class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selected: 'Home'
        }
    }
    setSelected = (tab) => {
        this.setState({selected: tab})
    }
    render() {
        return ( <div className='mainzz'>
            <h1>Hello World</h1>
            <TopBlog tabs={['Home','Settings','Profile']} selected={this.state.selected} setSelected={this.setSelected}>
                <Tab isSelected={this.state.setSelected === 'Home'}>
                    <p>Lorem, ipsum.</p>
                </Tab>
                <Tab isSelected={this.state.setSelected === 'Settings'}>
                    <p>Lorem, Lorem, ipsum dolor. ipsum.</p>
                </Tab>
                <Tab isSelected={this.state.setSelected === 'Profile'}>
                    <p>Lpsum dolor. ipsum.</p>
                </Tab>
            </TopBlog>
        </div> );
    }
    handleDelete = () => {
        this.props.onDelete(this.props.char);
    }
  
}
 
export default Main;
